package oopsconcept;

public class Calculator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	}
	public void add(int n,int a)
	{
		int sum = n + a;
		System.out.println("Adding Two Number: "+sum);
	}
	public void sub(int num1,int num2)
	{
		int sum = num1 - num2;
		System.out.println("subtracting Two Number: "+sum);
	}
	public void mul(int num1,int num2)
	{
		int sum = num1 * num2;
		System.out.println("multile Two Number: "+sum);
	}
	public void div(int num1,int num2)
	{
		int sum = num1 / num2;
		System.out.println("dividing Two Number: "+sum);
	}
}
